// store/index.js
import { createStore } from 'vuex';

export default createStore({
  state: {
    // 定义state
    selections: {
      selection1: '',
      selection2: '',
      selection3: '',
      selection4: [],
    },
    // ...其他state
  },
  mutations: {
    // 更新选择框的值
    updateSelections(state, { selection1, selection2, selection3, selection4 }) {
      state.selections.selection1 = selection1;
      state.selections.selection2 = selection2;
      state.selections.selection3 = selection3;
      state.selections.selection4 = selection4;
    },
    // ...其他mutations
  },
});
