// store.js
import Vue from 'vue';
import Vuex from 'vuex';

Vue.use(Vuex);

export default new Vuex.Store({
  state: {
    selections: {
      selection1: '',
      selection2: '',
      selection3: '',
      selection4: [],
    },
  },
  mutations: {
    setSelection(state, { key, value }) {
      Vue.set(state.selections, key, value);
    },
  },
});
