// store.js
import Vue from 'vue';
import Vuex from 'vuex';

Vue.use(Vuex);

export default new Vuex.Store({
  state: {
    selections: {
      selection1: null,
      selection2: null,
      selection3: null,
      selection4: null,
    },
  },
  mutations: {
    setSelection(state, { key, value }) {
      state.selections[key] = value;
    },
  },
  actions: {
    updateSelection({ commit }, payload) {
      commit('setSelection', payload);
    },
  },
});
